    orgId: terraformTest.id,
    name: "sso_admin_one",
    customLogoutUrl: "https://idp.com/logout",
    idpCert: "-----BEGIN CERTIFICATE-----MIIF0jC .../fSCGx7-----END CERTIFICATE-----",
    idpSignAlgo: "sha512",
    idpSsoUrl: "https://idp.com/login",
    issuer: "my_idp_issuer",
    nameidFormat: "email",
});
